import React from 'react'
import './styles.scss';

function CardItem({ src, type, title, subtitle }) {
    return (
        <div className={type === "large" ? "CardItem--Large" : "CardItem--Medium"}>
            <div className="CardItem--label">
                <img src={src} alt={title} />
            </div>
            <h3>{subtitle}</h3>
        </div>
    )
}

export default CardItem
